mapper.select:
select *
from red_packet
where red_packet_no = #{redPacketNo}

  and is_over=#{isOver}

mapper.update:
update red_packet
set
is_over = '2',gmt_modify =now(),end_time=now()

  , is_over_time=#{isOverTime}

where red_packet_no = #{redPacketNo}

update red_packet
set
gmt_modify =now()

  ,is_over=#{isOver}


  ,tx_hash=#{txHash}


  ,status=#{status}


  ,start_time=#{startTime}

where red_packet_no = #{redPacketNo}
